package switchcode;

public class switchcode {

	public static void main(String[] args) {
		
		int day = 2;
		
		switch (day) {
		
		case 1: System.out.println("saturday");
		break;
		
		case 2: System.out.println("sunday");
		break;
		case 3: System.out.println("monday");
		break;
		
		
				
		
		}
		// TODO Auto-generated method stub
		
		int i = 0;
		do {System.out.println("value "+i);
		i++; }
		while(i<5);
		
		
		
		
		
		for (int x = 0 ; x<=70; x= x+5)
		
		{System.out.println(x);
		}
		
		int m = 15;
		
		if (m<20);{
			System.out.println("smaller num"+m);
		}
		
		
		}
	}


